    def get_response(self, user_input):
        #AIML input
        aiml_response = self.kernel.respond(user_input)
        return aiml_response 

    def get_chatbot_response(self, user_input):
        
        # Calculate cosine similarity between user input and documents
        user_vector = self.tfidf_vectorizer.transform([user_input])
        similarities = cosine_similarity(user_vector, self.tfidf_matrix)

        # Get the most similar document and its corresponding text
        most_similar_index = similarities.argmax()
        most_similar_text = self.documents[most_similar_index]

        # Get the corresponding cosine similarity score
        cosine_similarity_score = similarities[0, most_similar_index]

        return most_similar_text, cosine_similarity_score
    
    def initialize_features(self):
        # Step 1: Tokenization
        nltk.download("punkt")

        # Step 2: Stopword Removal
        nltk.download("stopwords")
        self.stop_words = set(stopwords.words("english"))

        # Step 3: Stemming
        self.stemmer = PorterStemmer()

        # Step 4: TF-IDF Vectorization
        preprocessed_documents = [" ".join([self.stemmer.stem(word) for word in word_tokenize(doc.lower()) if word not in self.stop_words]) for doc in self.documents]

        self.tfidf_vectorizer = TfidfVectorizer()
        self.tfidf_matrix = self.tfidf_vectorizer.fit_transform(preprocessed_documents)

        # Step 5: LSA Dimensionality Reduction
        lsa = TruncatedSVD(n_components=2)
        self.lsa_matrix = lsa.fit_transform(self.tfidf_matrix)

        # Initialize variables to keep track of metrics
        self.exact_matches = 0

        # Set the threshold for cosine similarity classification
        self.threshold = 0.25  
        
    def run_chatbot(self):    
        # Start the conversation loop
        print("Chatbot: Hello! How can I assist you? (Type 'exit' to end the conversation)")
        while True:
            true_positives = 0
            false_positives = 0
            false_negatives = 0
            exact_matches = 0

            user_input = input("You: ")

            if user_input.lower() == 'exit':
                print("Chatbot: Goodbye! Have a great day!")
                break

            # Preprocess the user input
            preprocessed_user_input = " ".join([self.stemmer.stem(word) for word in word_tokenize(user_input.lower()) if word not in self.stop_words])

            # Calculate cosine similarity between user input and documents
            user_vector = self.tfidf_vectorizer.transform([preprocessed_user_input])
            similarities = cosine_similarity(user_vector, self.tfidf_matrix)

            # Get the most similar document and its corresponding text
            most_similar_index = similarities.argmax()
            most_similar_text = self.documents[most_similar_index]

            # Get the chatbot response and the cosine similarity score
            chatbot_response, cosine_similarity_score = self.get_chatbot_response(preprocessed_user_input)

            # AIML result
            aiml_response = self.get_response(user_input)

